                    <paragrahv id="para59">
                        <paragrahvNr>59</paragrahvNr>
                        <kuvatavNr><![CDATA[§ 59.]]></kuvatavNr>
                        <paragrahvPealkiri>Lepingu täitmine</paragrahvPealkiri>
                        <muutmismarge>
                            <tavatekst>Kehtetu - </tavatekst>
                            <avaldamismarge>
                                <RTosa>RT I</RTosa>
                                <avaldamineKuupaev>2013-12-31</avaldamineKuupaev>
                                <RTartikkel>1</RTartikkel>
                                <aktViide>131122013001</aktViide>
                            </avaldamismarge>
