                                <avaldamismarge>
                                    <RTosa>RT I</RTosa>
                                    <RTaasta>2010</RTaasta>
                                    <RTnr>77</RTnr>
                                    <RTartikkel>590</RTartikkel>
                                    <aktViide>13364145</aktViide>
                                </avaldamismarge>
                                <joustumine>2011-07-01</joustumine>
                            </muutmismarge>
                        </loige>
                        <loige id="para404lg4">
                            <loigeNr>4</loigeNr>
                            <kuvatavNr><![CDATA[(4)]]></kuvatavNr>
                            <muutmismarge>
                                <tavatekst>Kehtetu - </tavatekst>
                                <avaldamismarge>
                                    <RTosa>RT I</RTosa>
                                    <RTaasta>2010</RTaasta>
                                    <RTnr>77</RTnr>
